import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Importation d'Axios
import Headline from '../../layouts/form/headline';
import ProgressBar from './progressbare';
import Footerform from '../../layouts/form/footerform';

function FormulaireDesignCircularity() {
  const [progress, setProgress] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');
  const [formValues, setFormValues] = useState({
    postalAddress: '',
    emailAddress: '',
    webAddress: '',
    partNumbers: '',
    manualRemovalBattery: false,
    manualDisassemblyBatteryPack: false,
    extinguishingAgent: '',
    safetyMeasures: '',
    preConsumerRecycledNickel: '',
    preConsumerRecycledCobalt: '',
    preConsumerRecycledLithium: '',
    preConsumerRecycledLead: '',
    postConsumerRecycledNickel: '',
    postConsumerRecycledCobalt: '',
    renewableContentShare: '',
    postConsumerRecycledLeadShare: '',
    postConsumerRecycledLithiumShare: '',
    wastePrevention: '',
    wasteBatteries: '',
    separateCollection: ''
  });

  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault(); // Empêcher le comportement par défaut du formulaire

    const data = {
      DesignofCircularity: {
        postalAddress: formValues.postalAddress,
        emailAddress: formValues.emailAddress,
        webAddress: formValues.webAddress,
        partNumbers: formValues.partNumbers,
        manualRemovalBattery: formValues.manualRemovalBattery,
        manualDisassemblyBatteryPack: formValues.manualDisassemblyBatteryPack,
      },
      batterySafetyRequirements: {
        extinguishingAgent: formValues.extinguishingAgent,
        safetyMeasures: formValues.safetyMeasures,
      },
      recycledAndRenewableContent: {
        preConsumerRecycledNickel: formValues.preConsumerRecycledNickel,
        preConsumerRecycledCobalt: formValues.preConsumerRecycledCobalt,
        preConsumerRecycledLithium: formValues.preConsumerRecycledLithium,
        preConsumerRecycledLead: formValues.preConsumerRecycledLead,
        postConsumerRecycledNickel: formValues.postConsumerRecycledNickel,
        postConsumerRecycledCobalt: formValues.postConsumerRecycledCobalt,
        renewableContentShare: formValues.renewableContentShare,
        postConsumerRecycledLeadShare: formValues.postConsumerRecycledLeadShare,
        postConsumerRecycledLithiumShare: formValues.postConsumerRecycledLithiumShare,
      },
      endOfLifeInformation: {
        wastePrevention: formValues.wastePrevention,
        wasteBatteries: formValues.wasteBatteries,
        separateCollection: formValues.separateCollection,
      }
    };

    try {
      await axios.post('http://localhost:5555/api/design-circularity', data, {
        headers: {
          'Content-Type': 'application/json'
        }
      });
      return true; // Indiquer que la soumission a réussi
    } catch (err) {
      console.error('Erreur lors de la soumission du formulaire:', err);
      setErrorMessage('Erreur lors de la soumission du formulaire. Veuillez réessayer.');
      return false; // Indiquer que la soumission a échoué
    }
  };

  const handleNext = async (event) => {
    event.preventDefault(); // Empêcher le comportement par défaut du bouton si utilisé directement

    setProgress(100); // Mettre à jour la progression

    const submissionSuccessful = await handleSubmit(); // Soumettre le formulaire et obtenir le résultat

    if (submissionSuccessful) {
      navigate('/batterymaterials'); // Redirection vers la page suivante en cas de succès
    }
  };

  const handlePrevious = () => {
    navigate('/supplychain');
  };

  const handleMouseEnter = (type) => {
    setTooltips((prev) => ({ ...prev, [type]: true }));
  };

  const handleMouseLeave = (type) => {
