import React, { useState } from 'react';

const ProgressBar = () => {
  const [progress, setProgress] = useState(0);

  const fillForm = (num) => {
    // Logique de remplissage du formulaire
    switch (num) {
      case 1:
        setProgress(20);
        break;
      case 2:
        setProgress(40);
        break;
      case 3:
        setProgress(60);
        break;
      case 4:
        setProgress(80);
        break;
      case 5:
        setProgress(100);
        break;
      default:
        break;
    }
  };

  return (
    <div className="w-full bg-gray-200 rounded-md h-8 mb-4">
      <div
        className="h-full rounded-md"
        style={{ width: `${progress}%`, transition: 'width 0.3s ease' }}
      >
        <div className="h-full flex justify-between">
          <div className="w-1/5 bg-red-500"></div> {/* Red */}
          <div className="w-1/5 bg-yellow-500"></div> {/* Yellow */}
          <div className="w-1/5 bg-green-500"></div> {/* Green */}
          <div className="w-1/5 bg-blue-500"></div> {/* Blue */}
          <div className="w-1/5 bg-purple-500"></div> {/* Purple */}
        </div>
      </div>
    </div>
  );
};

export default ProgressBar;
