// BarreProgression.js

import React, { useState } from 'react';

const Progressbar = () => {
    const [progress, setProgress] = useState(0);
    const [filledForms, setFilledForms] = useState([]);

    const forms = [
        { id: 1, label: 'General Information' },
        { id: 2, label: 'Supply Chain Due Diligence' },
        { id: 3, label: 'Design of Circularity' },
        { id: 4, label: 'Battery Materials and Composition' },
        { id: 5, label: 'Performance and Durability' }
    ];

    const handleCheckboxChange = (formId) => {
        let updatedForms = [...filledForms];
        if (updatedForms.includes(formId)) {
            updatedForms = updatedForms.filter(id => id !== formId);
        } else {
            updatedForms.push(formId);
        }

        const newProgress = (updatedForms.length / forms.length) * 100;
        setProgress(newProgress);
        setFilledForms(updatedForms);
    };

    return (
        <>
            <div className="w-full bg-gray-200 h-8 rounded-md overflow-hidden">
                <div className="h-full bg-green-500" style={{ width: `${progress}%` }}></div>
            </div>
            <div className="mt-4">
                {forms.map(form => (
                    <div key={form.id} className="flex items-center mb-2">
                        <input
                            type="checkbox"
                            id={form.id}
                            className="form-checkbox h-5 w-5 text-green-500"
                            onChange={() => handleCheckboxChange(form.id)}
                            checked={filledForms.includes(form.id)}
                        />
                        <label htmlFor={form.id} className="ml-2">{form.label}</label>
                    </div>
                ))}
            </div>
        </>
    );
};

export default Progressbar;
