import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    { text: 'general information', className: 'step step-info' },
    { text: 'supply chain due diligence', className: 'step step-info' },
    { text: 'design of circularity', className: 'step step-info' },
    { text: 'battery materials and composition', className: 'step step-info' },
    { text: 'performance and durability', className: 'step step-info' }, // Ajout de la nouvelle étape ici
  ];

  // Calculer la largeur maximale pour aligner les titres
  const maxTextLength = steps.reduce((max, step) => Math.max(max, step.text.length), 0);

  return (
    <ul className="steps">
      {steps.map((step, index) => (
        <li key={index} className={`${step.className} ${index <= currentStep ? 'font-bold' : 'font-normal'}`}>
          <div className="flex items-center">
            <span style={{ minWidth: `${maxTextLength}ch` }}>{step.text}</span>
            {step.dataContent && <span data-content={step.dataContent}></span>}
          </div>
          {index !== steps.length - 1 && (
            <div className={`mx-4 h-6 ${index < currentStep ? 'bg-green-500' : 'bg-gray-300'} flex-1`}></div>
          )}
        </li>
      ))}
    </ul>
  );
};

export default ProgressBar;
