import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Importation d'Axios
import Headline from '../../layouts/form/headline';
import ProgressBar from './progressbare';
import Footerform from '../../layouts/form/footerform';

function FormulaireDesignCircularity() {
  const [progress, setProgress] = useState(0);
  const navigate = useNavigate();
  const formRef = useRef(null); // Référence au formulaire


  // Gestion des envois de données
  const handleSubmit = async (event) => {
    

    const formData = new FormData(formRef.current);

    const data = {
      DesignofCircularity: {
        postalAddress: formData.get('postalAddress'),
        emailAddress: formData.get('emailAddress'),
        webAddress: formData.get('webAddress'),
        partNumbers: formData.get('partNumbers'),
        manualRemovalBattery: formData.get('manualRemovalBattery'),
        manualDisassemblyBatteryPack: formData.get('manualDisassemblyBatteryPack'),
      },
      batterySafetyRequirements: {
        extinguishingAgent: formData.get('extinguishingAgent'),
        safetyMeasures: formData.get('safetyMeasures'),
      },
      recycledAndRenewableContent: {
        preConsumerRecycledNickel: formData.get('preConsumerRecycledNickel'),
        preConsumerRecycledCobalt: formData.get('preConsumerRecycledCobalt'),
        preConsumerRecycledLithium: formData.get('preConsumerRecycledLithium'),
        preConsumerRecycledLead: formData.get('preConsumerRecycledLead'),
        postConsumerRecycledNickel: formData.get('postConsumerRecycledNickel'),
        postConsumerRecycledCobalt: formData.get('postConsumerRecycledCobalt'),
        renewableContentShare: formData.get('renewableContentShare'),
        postConsumerRecycledLeadShare: formData.get('postConsumerRecycledLeadShare'),
        postConsumerRecycledLithiumShare: formData.get('postConsumerRecycledLithiumShare'),
      },
      endOfLifeInformation: {
        wastePrevention: formData.get('wastePrevention'),
        wasteBatteries: formData.get('wasteBatteries'),
        separateCollection: formData.get('separateCollection'),
      }
    };

    try {
      // Envoyer les données à votre API backend
      await axios.post('http://localhost:5555/api/design-circularity', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      return true; // Indiquer que la soumission a réussi
    } catch (err) {
      console.error('Erreur lors de la soumission du formulaire:', err);
      return false; // Indiquer que la soumission a échoué
    }
  };

  const handleNext = async () => {
    setProgress(100); // Mettre à jour la progression

    const submissionSuccessful = await handleSubmit(); // Soumettre le formulaire et obtenir le résultat

    if (submissionSuccessful) {
      navigate('/batterymaterials'); // Redirection vers la page suivante en cas de succès
    } else {
      // Vous pouvez afficher un message d'erreur ici si la soumission échoue
    }
  };


  const handlePrevious = () => {
    navigate('/supplychain');
  };

  const handleMouseEnter = (type) => {
    setTooltips((prev) => ({ ...prev, [type]: true }));
  };

  const handleMouseLeave = (type) => {
    setTooltips((prev) => ({ ...prev, [type]: false }));
  };
  // État pour gérer l'affichage des tooltips
  const [tooltips, setTooltips] = useState({
    Postaladdress: false,
    Emailaddress: false,
    Webaddress: false,
    Partnumbers: false,
    ManualRemovalBattery: false,
    ManualDisassemblyBatteryPack: false,
    ExtinguishingAgent: false,
    SafetyMeasures: false,
    PreConsumerRecycledNickel: false,
    PreConsumerRecycledCobalt: false,
    PreConsumerRecycledLithium: false,
    PreConsumerRecycledLead: false,
    PostConsumerRecycledNickel: false,
    PostConsumerRecyclecobat:false,
    Renewablecontentshare:false,
