import React, { useState } from 'react';
import Headline from '../layouts/headline'; // Assurez-vous que le chemin est correct

function FormulairePerformanceDurability() {
  const [expectedLifetime, setExpectedLifetime] = useState('');
  const [cycleLifeTest, setCycleLifeTest] = useState('');
  const [cRate, setCRate] = useState('');
  const [capacityThreshold, setCapacityThreshold] = useState('');
  const [soceThreshold, setSoceThreshold] = useState('');
  const [warrantyPeriod, setWarrantyPeriod] = useState('');
  const [batteryServiceDate, setBatteryServiceDate] = useState('');
  const [ratedCapacity, setRatedCapacity] = useState('');
  const [nominalVoltage, setNominalVoltage] = useState('');
  const [minVoltage, setMinVoltage] = useState('');
  const [maxVoltage, setMaxVoltage] = useState('');
  const [originalPowerCapability, setOriginalPowerCapability] = useState('');
  const [maxBatteryPower, setMaxBatteryPower] = useState('');
  const [idleStateLowerBoundary, setIdleStateLowerBoundary] = useState('');
  const [idleStateUpperBoundary, setIdleStateUpperBoundary] = useState('');
  const [initialInternalResistanceCell, setInitialInternalResistanceCell] = useState('');
  const [initialInternalResistancePack, setInitialInternalResistancePack] = useState('');
  const [initialSelfDischargingRate, setInitialSelfDischargingRate] = useState('');
  const [initialRoundTripEfficiency, setInitialRoundTripEfficiency] = useState('');
  const [roundTripEfficiencyAt50, setRoundTripEfficiencyAt50] = useState('');
  const [accidentInformation, setAccidentInformation] = useState('');
  const [deepDischargeEvents, setDeepDischargeEvents] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Submitted:', {
      expectedLifetime,
      cycleLifeTest,
      cRate,
      capacityThreshold,
      soceThreshold,
      warrantyPeriod,
      batteryServiceDate,
      ratedCapacity,
      nominalVoltage,
      minVoltage,
      maxVoltage,
      originalPowerCapability,
      maxBatteryPower,
      idleStateLowerBoundary,
      idleStateUpperBoundary,
      initialInternalResistanceCell,
      initialInternalResistancePack,
      initialSelfDischargingRate,
      initialRoundTripEfficiency,
      roundTripEfficiencyAt50,
      accidentInformation,
      deepDischargeEvents,
    });
    // Réinitialisez les champs après la soumission si nécessaire
    // setExpectedLifetime('');
    // setCycleLifeTest('');
    // setCRate('');
    // setCapacityThreshold('');
    // setSoceThreshold('');
    // setWarrantyPeriod('');
    // setBatteryServiceDate('');
    // setRatedCapacity('');
    // setNominalVoltage('');
    // setMinVoltage('');
    // setMaxVoltage('');
    // setOriginalPowerCapability('');
    // setMaxBatteryPower('');
    // setIdleStateLowerBoundary('');
    // setIdleStateUpperBoundary('');
    // setInitialInternalResistanceCell('');
    // setInitialInternalResistancePack('');
    // setInitialSelfDischargingRate('');
    // setInitialRoundTripEfficiency('');
    // setRoundTripEfficiencyAt50('');
    // setAccidentInformation('');
    // setDeepDischargeEvents('');
  };

  return (
    <div>
      <Headline />
      <div className="max-w-3xl mx-auto mt-8 p-6 bg-blue-50 rounded shadow-lg">
        <h2 className="text-xl font-bold mb-4 text-center">Performance and Durability</h2>

        {/* Sous-formulaire 1: Battery lifetime */}
        <form onSubmit={handleSubmit} className="bg-white rounded-lg p-4 shadow-sm mb-8">
          <h3 className="text-lg font-semibold mb-2">Battery lifetime</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-4">
            <div className="flex flex-col space-y-4">
              <div className="flex items-center space-x-4">
                <label htmlFor="expectedLifetime" className="text-sm text-gray-700 w-48">Expected lifetime: Number of charge-discharge cycles</label>
                <input
                  type="text"
                  id="expectedLifetime"
                  value={expectedLifetime}
                  onChange={(e) => setExpectedLifetime(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="cycleLifeTest" className="text-sm text-gray-700 w-48">Cycle-life reference test</label>
                <input
                  type="text"
                  id="cycleLifeTest"
                  value={cycleLifeTest}
                  onChange={(e) => setCycleLifeTest(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="cRate" className="text-sm text-gray-700 w-48">C-rate of relevant cycle-life test</label>
                <input
                  type="text"
                  id="cRate"
                  value={cRate}
                  onChange={(e) => setCRate(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="capacityThreshold" className="text-sm text-gray-700 w-48">Capacity threshold for exhaustion</label>
                <input
                  type="text"
                  id="capacityThreshold"
                  value={capacityThreshold}
                  onChange={(e) => setCapacityThreshold(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="soceThreshold" className="text-sm text-gray-700 w-48">SOCE threshold for exhaustion</label>
                <input
                  type="text"
                  id="soceThreshold"
                  value={soceThreshold}
                  onChange={(e) => setSoceThreshold(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="warrantyPeriod" className="text-sm text-gray-700 w-48">Warranty period of the battery</label>
                <input
                  type="text"
                  id="warrantyPeriod"
                  value={warrantyPeriod}
                  onChange={(e) => setWarrantyPeriod(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="batteryServiceDate" className="text-sm text-gray-700 w-48">Date of putting the battery into service</label>
                <input
                  type="date"
                  id="batteryServiceDate"
                  value={batteryServiceDate}
                  onChange={(e) => setBatteryServiceDate(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
            </div>
          </div>
        </form>

        {/* Sous-formulaire 2: Capacity voltage & power capability */}
        <form onSubmit={handleSubmit} className="bg-white rounded-lg p-4 shadow-sm mb-8">
          <h3 className="text-lg font-semibold mb-2">Capacity voltage & power capability</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-4">
            <div className="flex flex-col space-y-4">
              <div className="flex items-center space-x-4">
                <label htmlFor="ratedCapacity" className="text-sm text-gray-700 w-48">Rated capacity</label>
                <input
                  type="text"
                  id="ratedCapacity"
                  value={ratedCapacity}
                  onChange={(e) => setRatedCapacity(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="nominalVoltage" className="text-sm text-gray-700 w-48">Nominal voltage</label>
                <input
                  type="text"
                  id="nominalVoltage"
                  value={nominalVoltage}
                  onChange={(e) => setNominalVoltage(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="minVoltage" className="text-sm text-gray-700 w-48">Minimum voltage for individual cell</label>
                <input
                  type="text"
                  id="minVoltage"
                  value={minVoltage}
                  onChange={(e) => setMinVoltage(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="maxVoltage" className="text-sm text-gray-700 w-48">Maximum voltage for individual cell</label>
                <input
                  type="text"
                  id="maxVoltage"
                  value={maxVoltage}
                  onChange={(e) => setMaxVoltage(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="originalPowerCapability" className="text-sm text-gray-700 w-48">Original power capability</label>
                <input
                  type="text"
                  id="originalPowerCapability"
                  value={originalPowerCapability}
                  onChange={(e) => setOriginalPowerCapability(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="maxBatteryPower" className="text-sm text-gray-700 w-48">Maximum battery power</label>
                <input
                  type="text"
                  id="maxBatteryPower"
                  value={maxBatteryPower}
                  onChange={(e) => setMaxBatteryPower(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="idleStateLowerBoundary" className="text-sm text-gray-700 w-48">Idle state lower boundary</label>
                <input
                  type="text"
                  id="idleStateLowerBoundary"
                  value={idleStateLowerBoundary}
                  onChange={(e) => setIdleStateLowerBoundary(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="idleStateUpperBoundary" className="text-sm text-gray-700 w-48">Idle state upper boundary</label>
                <input
                  type="text"
                  id="idleStateUpperBoundary"
                  value={idleStateUpperBoundary}
                  onChange={(e) => setIdleStateUpperBoundary(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
            </div>
          </div>
        </form>

        {/* Sous-formulaire 3: Internal resistance, self-discharge rate & round-trip efficiency */}
        <form onSubmit={handleSubmit} className="bg-white rounded-lg p-4 shadow-sm mb-8">
          <h3 className="text-lg font-semibold mb-2">Internal resistance, self-discharge rate & round-trip efficiency</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-4">
            <div className="flex flex-col space-y-4">
              <div className="flex items-center space-x-4">
                <label htmlFor="initialInternalResistanceCell" className="text-sm text-gray-700 w-48">Initial internal resistance cell</label>
                <input
                  type="text"
                  id="initialInternalResistanceCell"
                  value={initialInternalResistanceCell}
                  onChange={(e) => setInitialInternalResistanceCell(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="initialInternalResistancePack" className="text-sm text-gray-700 w-48">Initial internal resistance pack</label>
                <input
                  type="text"
                  id="initialInternalResistancePack"
                  value={initialInternalResistancePack}
                  onChange={(e) => setInitialInternalResistancePack(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="initialSelfDischargingRate" className="text-sm text-gray-700 w-48">Initial self-discharging rate</label>
                <input
                  type="text"
                  id="initialSelfDischargingRate"
                  value={initialSelfDischargingRate}
                  onChange={(e) => setInitialSelfDischargingRate(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="initialRoundTripEfficiency" className="text-sm text-gray-700 w-48">Initial round-trip efficiency</label>
                <input
                  type="text"
                  id="initialRoundTripEfficiency"
                  value={initialRoundTripEfficiency}
                  onChange={(e) => setInitialRoundTripEfficiency(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="roundTripEfficiencyAt50" className="text-sm text-gray-700 w-48">Round-trip efficiency at 50% DOD</label>
                <input
                  type="text"
                  id="roundTripEfficiencyAt50"
                  value={roundTripEfficiencyAt50}
                  onChange={(e) => setRoundTripEfficiencyAt50(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
            </div>
          </div>
        </form>

        {/* Sous-formulaire 4: Accidents and deep discharge events */}
        <form onSubmit={handleSubmit} className="bg-white rounded-lg p-4 shadow-sm mb-8">
          <h3 className="text-lg font-semibold mb-2">Accidents and deep discharge events</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-4">
            <div className="flex flex-col space-y-4">
              <div className="flex items-center space-x-4">
                <label htmlFor="accidentInformation" className="text-sm text-gray-700 w-48">Accident information</label>
                <input
                  type="text"
                  id="accidentInformation"
                  value={accidentInformation}
                  onChange={(e) => setAccidentInformation(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
              <div className="flex items-center space-x-4">
                <label htmlFor="deepDischargeEvents" className="text-sm text-gray-700 w-48">Deep discharge events</label>
                <input
                  type="text"
                  id="deepDischargeEvents"
                  value={deepDischargeEvents}
                  onChange={(e) => setDeepDischargeEvents(e.target.value)}
                  placeholder="Type here"
                  className="input input-bordered input-primary w-full max-w-xs"
                  required
                />
              </div>
            </div>
          </div>
        </form>

        {/* Boutons de soumission */}
        <div className="flex justify-center space-x-4 mt-6">
          <button type="submit" onClick={handleSubmit} className="btn btn-primary">Submit</button>
          <button type="button" className="btn btn-secondary">Cancel</button>
        </div>
      </div>
    </div>
  );
}

export default FormulairePerformanceDurability;
