import React, { useState, useEffect } from 'react';

const ProgressBar = ({ currentStep, totalSteps }) => {
  // Calcul du pourcentage de progression
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    // Vérification pour éviter la division par zéro
    if (totalSteps > 0) {
      const newProgress = (currentStep / totalSteps) * 100;
      setProgress(newProgress);
    }
  }, [currentStep, totalSteps]);

  // Étapes du formulaire
  const steps = [
    'General Information',
    'Supply Chain Due Diligence',
    'Design of Circularity',
    'Battery Materials and Composition',
    'Performance and Durability'
  ];

  return (
    <div className="border-2 border-black rounded-lg p-4">
      <div className="relative h-4 bg-gray-200 rounded-lg overflow-hidden">
        <div
          className="absolute top-0 h-full bg-green-500"
          style={{ width: `${progress}%` }}
        ></div>
        {/* Marqueurs d'étape */}
        {steps.map((step, index) => (
          <div
            key={step}
            className={`absolute left-${(index / (steps.length - 1)) * 100}% transform -translate-x-1/2 h-4 w-4 bg-white border-2 border-gray-300 rounded-full`}
          ></div>
        ))}
      </div>
      {/* Noms des étapes */}
      <div className="flex justify-between mt-2 text-xs text-gray-600">
        {steps.map((step, index) => (
          <div
            key={step}
            className={`text-center w-1/5 ${
              index === currentStep - 1
                ? 'text-blue-500 font-bold'
                : index < currentStep - 1
                ? 'text-green-500 font-bold'
                : 'text-gray-500 font-light'
            }`}
          >
            {step}
          </div>
        ))}
      </div>
      {/* Affichage de l'étape actuelle */}
      <div className="text-sm text-gray-600 mt-1">
        {currentStep} / {totalSteps} formulaires complétés
      </div>
    </div>
  );
};

export default ProgressBar;
