// BarreProgression.js

import React, { useState } from 'react';

const Progressbar = () => {
    const [progress, setProgress] = useState(0);
    const [filledForms, setFilledForms] = useState([]);

    const forms = [
        { id: 1, label: 'General Information', color: 'bg-blue-500' },
        { id: 2, label: 'Supply Chain Due Diligence', color: 'bg-green-500' },
        { id: 3, label: 'Design of Circularity', color: 'bg-yellow-500' },
        { id: 4, label: 'Battery Materials and Composition', color: 'bg-red-500' },
        { id: 5, label: 'Performance and Durability', color: 'bg-purple-500' }
    ];

    const handleCheckboxChange = (formId) => {
        let updatedForms = [...filledForms];
        if (updatedForms.includes(formId)) {
            updatedForms = updatedForms.filter(id => id !== formId);
        } else {
            updatedForms.push(formId);
        }

        const newProgress = (updatedForms.length / forms.length) * 100;
        setProgress(newProgress);
        setFilledForms(updatedForms);
    };

    return (
        <div className="space-y-4">
            <div className="relative w-full h-8 rounded-md overflow-hidden bg-gray-200">
                {forms.map((form, index) => (
                    <React.Fragment key={form.id}>
                        <div className={`absolute left-0 top-0 h-full ${form.color}`} style={{ width: `${(100 / forms.length) * (index + 1)}%` }}></div>
                        <div className="absolute inset-0 flex items-center justify-center text-white font-bold">
                            {form.label}
                        </div>
                    </React.Fragment>
                ))}
            </div>
            <div className="mt-4">
                {forms.map(form => (
                    <div key={form.id} className="flex items-center">
                        <input
                            type="checkbox"
                            id={form.id}
                            className="form-checkbox h-5 w-5 text-green-500"
                            onChange={() => handleCheckboxChange(form.id)}
                            checked={filledForms.includes(form.id)}
                        />
                        <label htmlFor={form.id} className="ml-2">{form.label}</label>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Progressbar;
