import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Assurez-vous d'importer axios ou tout autre bibliothèque HTTP que vous utilisez

function FormulaireGeneralinformation() {
  const [batteryCategory, setBatteryCategory] = useState('');
  const [manufacturerId, setManufacturerId] = useState('');
  const [manufacturingDate, setManufacturingDate] = useState('');
  const [manufacturingPlace, setManufacturingPlace] = useState('');
  const [batteryWeight, setBatteryWeight] = useState('');
  const [euDeclarationFile, setEuDeclarationFile] = useState(null);
  const [idEuDeclaration, setIdEuDeclaration] = useState('');
  const [testReportsFile, setTestReportsFile] = useState(null);
  const [separateCollectionSymbol, setSeparateCollectionSymbol] = useState(null);
  const [cadmiumLeadSymbol, setCadmiumLeadSymbol] = useState(null);
  const [carbonFootprintClass, setCarbonFootprintClass] = useState('');
  const [co2Footprint, setCo2Footprint] = useState('');
  const [batteryPassportId, setBatteryPassportId] = useState(null);
  const [batteryIdentificationId, setBatteryIdentificationId] = useState(null);

  useEffect(() => {
    // Fonction asynchrone pour récupérer les IDs depuis votre backend
    const fetchIdsFromBackend = async () => {
      try {
        // Remplacez cette URL par l'URL de votre endpoint pour récupérer les IDs
        const response = await axios.get('https://votre-backend.com/api/getIds');
        const data = response.data;

        // Mettre à jour les états des IDs avec les valeurs récupérées
        setBatteryPassportId(data.batteryPassportId); // Assurez-vous que les noms correspondent à ceux de votre backend
        setBatteryIdentificationId(data.batteryIdentificationId); // Assurez-vous que les noms correspondent à ceux de votre backend
      } catch (error) {
        console.error('Error fetching IDs:', error);
        // Gérer les erreurs comme nécessaire
      }
    };

    // Appel de la fonction pour récupérer les IDs depuis le backend
    fetchIdsFromBackend();
  }, []); // Le tableau vide [] assure que useEffect s'exécute uniquement au montage initial

  const handleCategoryChange = (event) => {
    setBatteryCategory(event.target.value);
  };

  const handleManufacturerIdChange = (event) => {
    setManufacturerId(event.target.value);
  };

  const handleManufacturingDateChange = (event) => {
    setManufacturingDate(event.target.value);
  };

  const handleManufacturingPlaceChange = (event) => {
    setManufacturingPlace(event.target.value);
  };

  const handleBatteryWeightChange = (event) => {
    setBatteryWeight(event.target.value);
  };

  const handleEuDeclarationFileChange = (event) => {
    setEuDeclarationFile(event.target.files[0]);
  };

  const handleIdEuDeclarationChange = (event) => {
    setIdEuDeclaration(event.target.value);
  };

  const handleTestReportsFileChange = (event) => {
    setTestReportsFile(event.target.files[0]);
  };

  const handleSeparateCollectionSymbolChange = (event) => {
    setSeparateCollectionSymbol(event.target.files[0]);
  };

  const handleCadmiumLeadSymbolChange = (event) => {
    setCadmiumLeadSymbol(event.target.files[0]);
  };

  const handleCarbonFootprintClassChange = (event) => {
    setCarbonFootprintClass(event.target.value);
  };

  const handleCo2FootprintChange = (event) => {
    setCo2Footprint(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Vous pouvez traiter les données du formulaire ici, par exemple, les envoyer à un backend
    console.log('Submitted:', {
      batteryCategory,
      manufacturerId,
      manufacturingDate,
      manufacturingPlace,
      batteryWeight,
      euDeclarationFile,
      idEuDeclaration,
      testReportsFile,
      separateCollectionSymbol,
      cadmiumLeadSymbol,
      carbonFootprintClass,
      co2Footprint,
      batteryPassportId,
      batteryIdentificationId,
    });
    // Réinitialisez les champs après la soumission si nécessaire
    // setBatteryCategory('');
    // setManufacturerId('');
    // setManufacturingDate('');
    // ... etc.
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white shadow-md rounded-lg">
      <h2 className="text-lg font-semibold mb-4">Battery passport identification: {batteryPassportId}</h2>
      <h2 className="text-lg font-semibold mb-4">Battery identification: {batteryIdentificationId}</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="batteryCategory" className="block text-sm font-medium text-gray-700">
            Battery category:
          </label>
          <select
            id="batteryCategory"
            value={batteryCategory}
            onChange={handleCategoryChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          >
            <option value="">-- Sélectionnez une catégorie --</option>
            <option value="EV">EV</option>
            <option value="LMT">LMT</option>
            <option value="Industrial">Industrial</option>
            <option value="Stationary">Stationary</option>
          </select>
        </div>

        <div className="mb-4">
          <label htmlFor="manufacturerId" className="block text-sm font-medium text-gray-700">
            Manufacturer’s identification:
          </label>
          <input
            type="text"
            id="manufacturerId"
            value={manufacturerId}
            onChange={handleManufacturerIdChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="manufacturingDate" className="block text-sm font-medium text-gray-700">
            Manufacturing date:
          </label>
          <input
            type="date"
            id="manufacturingDate"
            value={manufacturingDate}
            onChange={handleManufacturingDateChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="manufacturingPlace" className="block text-sm font-medium text-gray-700">
            Manufacturing place:
          </label>
          <input
            type="text"
            id="manufacturingPlace"
            value={manufacturingPlace}
            onChange={handleManufacturingPlaceChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="batteryWeight" className="block text-sm font-medium text-gray-700">
            Battery weight:
          </label>
          <input
            type="text"
            id="batteryWeight"
            value={batteryWeight}
            onChange={handleBatteryWeightChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="euDeclarationFile" className="block text-sm font-medium text-gray-700">
            EU declaration of conformity:
          </label>
          <input
            type="file"
            id="euDeclarationFile"
            onChange={handleEuDeclarationFileChange}
            className="mt-1 block w-full"
            accept=".pdf,.doc,.docx"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="idEuDeclaration" className="block text-sm font-medium text-gray-700">
            ID of EU declaration of conformity:
          </label>
          <input
            type="text"
            id="idEuDeclaration"
            value={idEuDeclaration}
            onChange={handleIdEuDeclarationChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="testReportsFile" className="block text-sm font-medium text-gray-700">
            Results of tests reports:
          </label>
          <input
            type="file"
            id="testReportsFile"
            onChange={handleTestReportsFileChange}
            className="mt-1 block w-full"
            accept=".pdf,.doc,.docx"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="separateCollectionSymbol" className="block text-sm font-medium text-gray-700">
            Separate collection symbol:
          </label>
          <input
            type="file"
            id="separateCollectionSymbol"
            onChange={handleSeparateCollectionSymbolChange}
            className="mt-1 block w-full"
            accept="image/*"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="cadmiumLeadSymbol" className="block text-sm font-medium text-gray-700">
            Cadmium and lead symbols:
          </label>
          <input
            type="file"
            id="cadmiumLeadSymbol"
            onChange={handleCadmiumLeadSymbolChange}
            className="mt-1 block w-full"
            accept="image/*"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="carbonFootprintClass" className="block text-sm font-medium text-gray-700">
            Carbon footprint performance class:
          </label>
          <select
            id="carbonFootprintClass"
            value={carbonFootprintClass}
            onChange={handleCarbonFootprintClassChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          >
            <option value="">-- Sélectionnez une classe --</option>
            <option value="A">A</option>
            <option value="B">B</option>
            <option value="C">C</option>
            <option value="D">D</option>
            <option value="E">E</option>
            <option value="F">F</option>
            <option value="G">G</option>
          </select>
        </div>

        <div className="mb-4">
          <label htmlFor="co2Footprint" className="block text-sm font-medium text-gray-700">
            CO2 footprint:
          </label>
          <input
            type="text"
            id="co2Footprint"
            value={co2Footprint}
            onChange={handleCo2FootprintChange}
            className="mt-1 block w-full px-3 py-2 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            required
          />
        </div>

        <button
          type="submit"
          className="inline-block bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default FormulaireGeneralinformation;
