import React, { useState } from 'react'
import Headline from '../../layouts/form/headline'
import ProgressBar from './progressbare'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck, faQrcode } from '@fortawesome/free-solid-svg-icons'
import QRCode from 'qrcode'

const PublishedPassport = () => {
  const [nbrGenerated,setNbrGenerated] = useState(0)
    const [src,setSrc] = useState([])
    const generate =()=>{
      console.log(nbrGenerated)
      for(let i=0;i<nbrGenerated;i++){
        QRCode.toDataURL(`https://acme.dpp.Citet.com/${i}`).then((output)=>{src.push(output)})
      } 
      console.log(src)
      setSrc(src)
    }
  return (
    <div className="mt-8">
        <Headline />
        <div className="mt-8">
            <ProgressBar progress="6"/>
        </div>
        <div className="max-w-3xl mx-auto mt-8 p-6 bg-blue-50 rounded shadow-lg">
        <h2 className="text-xl font-bold mb-4 text-center text-success">Battery Passport successfully registred <FontAwesomeIcon icon={faCheck} /> !</h2>
        <form>
          <input type="text" placeholder='Number of batteries' className="input input-bordered w-full max-w-xs flex mx-auto m-4" value={nbrGenerated} onChange={(e)=>{setNbrGenerated(e.target.value)}} />
          <button type='button' className="btn btn-primary flex mx-auto" onClick={generate}>Generate QRCode</button>
        </form>
        {src.map((srcItem)=>{
          <div>
            <h2>hello</h2>
            <img src={srcItem} className='flex mx-auto p-2 w-[50%]'></img>
          </div>
          
        })}
        
        <button className="btn btn-active flex right-0"><a href='/dashboard'>Return to dashboard</a></button>
      </div>
      
    </div>
  )
}

export default PublishedPassport
