import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Importation d'Axios
import Headline from '../../layouts/form/headline';
import ProgressBar from './progressbare';
import Footerform from '../../layouts/form/footerform';

function FormulaireDesignCircularity() {
  const [progress, setProgress] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');
  const [tooltips, setTooltips] = useState({
    postalAddress: false,
    emailAddress: false,
    webAddress: false,
    partNumbers: false,
    manualRemovalBattery: false,
    manualDisassemblyBatteryPack: false,
    extinguishingAgent: false,
    safetyMeasures: false,
    preConsumerRecycledNickel: false,
    preConsumerRecycledCobalt: false,
    preConsumerRecycledLithium: false,
    preConsumerRecycledLead: false,
    postConsumerRecycledNickel: false,
    postConsumerRecycledCobalt: false,
    renewableContentShare: false,
    postConsumerRecycledLeadShare: false,
    postConsumerRecycledLithiumShare: false,
    wastePrevention: false,
    wasteBatteries: false,
    separateCollection: false
  });

  const navigate = useNavigate();
  const formRef = useRef(null); // Référence au formulaire

  const handleSubmit = async (event) => {
    
    const formData = new FormData();

    // Ajoute les valeurs du formulaire à FormData
    formData.append('postalAddress', formRef.current.querySelector('input[name="postalAddress"]').value);
    formData.append('emailAddress', formRef.current.querySelector('input[name="emailAddress"]').value);
    formData.append('webAddress', formRef.current.querySelector('input[name="webAddress"]').value);
    formData.append('partNumbers', formRef.current.querySelector('input[name="partNumbers"]').value);
    formData.append('manualRemovalBattery', formRef.current.querySelector('input[name="manualRemovalBattery"]').value);
    formData.append('manualDisassemblyBatteryPack', formRef.current.querySelector('input[name="manualDisassemblyBatteryPack"]').value);
    formData.append('extinguishingAgent', formRef.current.querySelector('input[name="extinguishingAgent"]').value);
    formData.append('safetyMeasures', formRef.current.querySelector('input[name="safetyMeasures"]').value);
    formData.append('preConsumerRecycledNickel', formRef.current.querySelector('input[name="preConsumerRecycledNickel"]').value);
    formData.append('preConsumerRecycledCobalt', formRef.current.querySelector('input[name="preConsumerRecycledCobalt"]').value);
    formData.append('preConsumerRecycledLithium', formRef.current.querySelector('input[name="preConsumerRecycledLithium"]').value);
    formData.append('preConsumerRecycledLead', formRef.current.querySelector('input[name="preConsumerRecycledLead"]').value);
    formData.append('postConsumerRecycledNickel', formRef.current.querySelector('input[name="postConsumerRecycledNickel"]').value);
    formData.append('postConsumerRecycledCobalt', formRef.current.querySelector('input[name="postConsumerRecycledCobalt"]').value);
    formData.append('renewableContentShare', formRef.current.querySelector('input[name="renewableContentShare"]').value);
