import React from 'react';

const ProgressBar = ({ currentStep }) => {
  const steps = [
    'General Information',
    'Supply Chain Due Diligence',
    'Design of Circularity',
    'Battery Materials and Composition',
    'Performance and Durability'
  ];

  // Tableau de couleurs pour chaque étape (à adapter selon vos préférences)
  const colors = [
    'bg-blue-200',   // Couleur pour 'General Information'
    'bg-green-200',  // Couleur pour 'Supply Chain Due Diligence'
    'bg-yellow-200', // Couleur pour 'Design of Circularity'
    'bg-red-200',    // Couleur pour 'Battery Materials and Composition'
    'bg-purple-200'  // Couleur pour 'Performance and Durability'
  ];

  return (
    <div className="border-2 border-black rounded-lg p-4">
      <div className="flex justify-between">
        {steps.map((step, index) => (
          <div key={step} className="flex items-center">
            <div className={`text-gray-600 ${index <= currentStep ? 'font-bold' : 'font-normal'}`}>{step}</div>
            {index !== steps.length - 1 && (
              <div className={`mx-4 h-6 ${index < currentStep ? colors[index] : 'bg-gray-300'} flex-1 rounded-full`}></div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProgressBar;
