import React, { useState } from 'react';

function FormulaireGeneralinformation() {
  const [batteryPassportIdentification, setBatteryPassportIdentification] = useState('');
  const [batteryIdentification, setBatteryIdentification] = useState('');
  const [batteryCategory, setBatteryCategory] = useState('');
  const [manufacturerId, setManufacturerId] = useState('');
  const [manufacturingDate, setManufacturingDate] = useState('');
  const [manufacturingPlace, setManufacturingPlace] = useState('');
  const [batteryWeight, setBatteryWeight] = useState('');
  const [carbonFootprintClass, setCarbonFootprintClass] = useState('');
  const [co2Footprint, setCo2Footprint] = useState('');

  const handleBatteryPassportIdentificationChange = (event) => {
    setBatteryPassportIdentification(event.target.value);
  };

  const handleBatteryIdentificationChange = (event) => {
    setBatteryIdentification(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setBatteryCategory(event.target.value);
  };

  const handleManufacturerIdChange = (event) => {
    setManufacturerId(event.target.value);
  };

  const handleManufacturingDateChange = (event) => {
    setManufacturingDate(event.target.value);
  };

  const handleManufacturingPlaceChange = (event) => {
    setManufacturingPlace(event.target.value);
  };

  const handleBatteryWeightChange = (event) => {
    setBatteryWeight(event.target.value);
  };

  const handleCarbonFootprintClassChange = (event) => {
    setCarbonFootprintClass(event.target.value);
  };

  const handleCo2FootprintChange = (event) => {
    setCo2Footprint(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Submitted:', {
      batteryPassportIdentification,
      batteryIdentification,
      batteryCategory,
      manufacturerId,
      manufacturingDate,
      manufacturingPlace,
      batteryWeight,
      carbonFootprintClass,
      co2Footprint,
    });
    // Réinitialisation des champs après soumission si nécessaire
    // ...
  };

  return (
    <div className="bg-gray-100 min-h-screen flex items-center justify-center p-4">
      <div className="max-w-4xl mx-auto p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-medium text-gray-800 mb-6 text-center">General Information</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Champs de saisie */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="mb-4">
              <label htmlFor="batteryPassportIdentification" className="block text-sm font-medium text-gray-700">
                Battery passport identification:
              </label>
              <input
                type="text"
                id="batteryPassportIdentification"
                value={batteryPassportIdentification}
                onChange={handleBatteryPassportIdentificationChange}
                className="input"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="batteryIdentification" className="block text-sm font-medium text-gray-700">
                Battery identification:
              </label>
              <input
                type="text"
                id="batteryIdentification"
                value={batteryIdentification}
                onChange={handleBatteryIdentificationChange}
                className="input"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="batteryCategory" className="block text-sm font-medium text-gray-700">
                Battery category:
              </label>
              <select
                id="batteryCategory"
                value={batteryCategory}
                onChange={handleCategoryChange}
                className="input"
                required
              >
                <option value="">-- Select a category --</option>
                <option value="EV">EV</option>
                <option value="LMT">LMT</option>
                <option value="Industrial">Industrial</option>
                <option value="Stationary">Stationary</option>
              </select>
            </div>

            <div className="mb-4">
              <label htmlFor="manufacturerId" className="block text-sm font-medium text-gray-700">
                Manufacturer’s identification:
              </label>
              <input
                type="text"
                id="manufacturerId"
                value={manufacturerId}
                onChange={handleManufacturerIdChange}
                className="input"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="manufacturingDate" className="block text-sm font-medium text-gray-700">
                Manufacturing date:
              </label>
              <input
                type="date"
                id="manufacturingDate"
                value={manufacturingDate}
                onChange={handleManufacturingDateChange}
                className="input"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="manufacturingPlace" className="block text-sm font-medium text-gray-700">
                Manufacturing place:
              </label>
              <input
                type="text"
                id="manufacturingPlace"
                value={manufacturingPlace}
                onChange={handleManufacturingPlaceChange}
                className="input"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="batteryWeight" className="block text-sm font-medium text-gray-700">
                Battery weight:
              </label>
              <input
                type="text"
                id="batteryWeight"
                value={batteryWeight}
                onChange={handleBatteryWeightChange}
                className="input"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="carbonFootprintClass" className="block text-sm font-medium text-gray-700">
                Carbon footprint class:
              </label>
              <input
                type="text"
                id="carbonFootprintClass"
                value={carbonFootprintClass}
                onChange={handleCarbonFootprintClassChange}
                className="input"
                required
              />
            </div>

            <div className="mb-4">
              <label htmlFor="co2Footprint" className="block text-sm font-medium text-gray-700">
                CO2 footprint:
              </label>
              <input
                type="text"
                id="co2Footprint"
                value={co2Footprint}
                onChange={handleCo2FootprintChange}
                className="input"
                required
              />
            </div>
          </div>

          {/* Boutons de soumission et annulation */}
          <div className="flex justify-center">
            <button type="submit" className="btn btn-primary px-8 py-3 mr-2">
              Submit
            </button>
            <button type="button" className="btn btn-secondary px-8 py-3">
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default FormulaireGeneralinformation;
