import React from 'react';

function FormulaireDesignCircularity() {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-screen-lg p-6">
        <h2 className="text-center mb-8 text-3xl font-bold text-gray-800">Design of Circularity</h2>

        {/* Sous-formulaire 1: Design of Circularity */}
        <div className="mb-8 bg-white rounded-lg p-6 shadow-md">
          <h3 className="text-lg font-semibold mb-4 text-gray-800">Design of Circularity</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-4">
              <FormField label="Postal address of sources for spare parts" />
              <FormField label="E-mail address of sources for spare parts" />
              <FormField label="Web address of sources for spare parts" />
              <FormField label="Part numbers for components" />
              <FileUploadField label="Manual for removal of the battery from the appliance" />
              <FileUploadField label="Manual for disassembly and dismantling of the battery pack" />
            </div>
          </div>
        </div>

        {/* Ajouter d'autres sous-formulaires de la même manière */}

        {/* Bouton de soumission */}
        <div className="flex justify-end">
          <button className="btn-primary">Submit</button>
        </div>
      </div>
    </div>
  );
}

// Composant pour les champs de formulaire texte
const FormField = ({ label }) => (
  <div className="flex flex-col">
    <label className="text-sm text-gray-700 mb-1">{label}</label>
    <input type="text" placeholder="Type here" className="input-primary" />
  </div>
);

// Composant pour les champs de téléchargement de fichier
const FileUploadField = ({ label }) => (
  <div className="flex flex-col">
    <label className="text-sm text-gray-700 mb-1">{label}</label>
    <input type="file" className="file-input" />
  </div>
);

export default FormulaireDesignCircularity;
